import React, { useState } from 'react';
import axios from 'axios';

export default function AddTaskPage() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const handleAdd = async () => {
    try {
      if (!title.trim()) {
        alert('Title is required');
        return;
      }
      await axios.post('http://localhost:5000/tasks', { title, description });
      alert('Task added!');
      setTitle('');
      setDescription('');
    } catch {
      alert('Error adding task');
    }
  };

  return (
    <div
      style={{
        padding: '30px',
        maxWidth: '600px',
        margin: '50px auto',
        backgroundColor: '#ffffff',
        borderRadius: '10px',
        boxShadow: '0 10px 20px rgba(0, 0, 0, 0.1)',
        background: 'linear-gradient(135deg, #ff7e5f, #feb47b)',
        color: '#fff',
      }}
    >
      <h2
        style={{
          fontSize: '28px',
          fontWeight: 'bold',
          marginBottom: '25px',
          textAlign: 'center',
        }}
      >
        Add New Task
      </h2>
      <input
        style={{
          border: '2px solid #f5a623',
          width: '100%',
          padding: '12px 18px',
          marginBottom: '20px',
          borderRadius: '5px',
          fontSize: '16px',
          backgroundColor: '#f7f7f7',
          transition: '0.3s',
        }}
        placeholder="Enter task title"
        value={title}
        onChange={e => setTitle(e.target.value)}
        onFocus={(e) => e.target.style.backgroundColor = '#fff'}
        onBlur={(e) => e.target.style.backgroundColor = '#f7f7f7'}
      />
      <textarea
        style={{
          border: '2px solid #f5a623',
          width: '100%',
          padding: '12px 18px',
          marginBottom: '30px',
          borderRadius: '5px',
          fontSize: '16px',
          backgroundColor: '#f7f7f7',
          minHeight: '150px',
          transition: '0.3s',
        }}
        placeholder="Enter task description"
        value={description}
        onChange={e => setDescription(e.target.value)}
        onFocus={(e) => e.target.style.backgroundColor = '#fff'}
        onBlur={(e) => e.target.style.backgroundColor = '#f7f7f7'}
      />
      <button
        style={{
          backgroundColor: '#ff7e5f',
          color: '#fff',
          padding: '15px 25px',
          border: 'none',
          borderRadius: '5px',
          fontSize: '18px',
          cursor: 'pointer',
          width: '100%',
          transition: 'background-color 0.3s, transform 0.3s',
        }}
        onClick={handleAdd}
        onMouseEnter={(e) => e.target.style.backgroundColor = '#feb47b'}
        onMouseLeave={(e) => e.target.style.backgroundColor = '#ff7e5f'}
      >
        Add Task
      </button>
    </div>
  );
}
