import React, { useState } from 'react';
import axios from 'axios';

export default function AddTaskPage() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const handleAdd = async () => {
    try {
      if (!title.trim()) {
        alert('Title is required');
        return;
      }
      await axios.post('http://localhost:5000/tasks', { title, description });
      alert('Task added!');
      setTitle('');
      setDescription('');
    } catch {
      alert('Error adding task');
    }
  };

  return (
    <div
      style={{
        padding: '20px',
        maxWidth: '500px',
        margin: '0 auto',
        backgroundColor: '#f9f9f9',
        borderRadius: '8px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
      }}
    >
      <h2
        style={{
          fontSize: '24px',
          fontWeight: 'bold',
          marginBottom: '20px',
          color: '#333',
        }}
      >
        Add Task
      </h2>
      <input
        style={{
          border: '1px solid #ddd',
          width: '100%',
          padding: '10px',
          marginBottom: '15px',
          borderRadius: '4px',
          fontSize: '16px',
        }}
        placeholder="Title"
        value={title}
        onChange={e => setTitle(e.target.value)}
      />
      <textarea
        style={{
          border: '1px solid #ddd',
          width: '100%',
          padding: '10px',
          marginBottom: '20px',
          borderRadius: '4px',
          fontSize: '16px',
          minHeight: '100px',
        }}
        placeholder="Description"
        value={description}
        onChange={e => setDescription(e.target.value)}
      />
      <button
        style={{
          backgroundColor: '#007BFF',
          color: '#fff',
          padding: '12px 24px',
          border: 'none',
          borderRadius: '4px',
          fontSize: '16px',
          cursor: 'pointer',
          transition: 'background-color 0.3s',
        }}
        onClick={handleAdd}
      >
        Add Task
      </button>
    </div>
  );
}
