import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function ManageTasksPage() {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = () => {
    axios.get('http://localhost:5000/tasks').then(res => setTasks(res.data));
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const deleteTask = async id => {
    await axios.delete(`http://localhost:5000/tasks/${id}`);
    fetchTasks();
  };

  const toggleComplete = async (id, completed) => {
    await axios.put(`http://localhost:5000/tasks/${id}`, { completed: !completed });
    fetchTasks();
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Manage Tasks</h2>
      {tasks.map(task => (
        <div key={task.id} className="border p-2 mb-2">
          <strong>{task.title}</strong><br />
          {task.description}<br />
          <span>Status: {task.completed ? '✅' : '❌'}</span><br />
          <button onClick={() => toggleComplete(task.id, task.completed)} className="bg-green-500 text-white px-2 py-1 mr-2">Toggle</button>
          <button onClick={() => deleteTask(task.id)} className="bg-red-500 text-white px-2 py-1">Delete</button>
        </div>
      ))}
    </div>
  );
}
