import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function ManageTasksPage() {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = () => {
    axios.get('http://localhost:5000/tasks').then(res => setTasks(res.data));
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const deleteTask = async id => {
    await axios.delete(`http://localhost:5000/tasks/${id}`);
    fetchTasks();
  };

  const toggleComplete = async (id, completed) => {
    await axios.put(`http://localhost:5000/tasks/${id}`, { completed: !completed });
    fetchTasks();
  };

  return (
    <div
      style={{
        padding: '40px',
        maxWidth: '800px',
        margin: '50px auto',
        backgroundColor: '#fff',
        borderRadius: '8px',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
      }}
    >
      <h2
        style={{
          fontSize: '24px',
          fontWeight: '600',
          color: '#333',
          marginBottom: '20px',
          textAlign: 'center',
        }}
      >
        Manage Tasks
      </h2>
      {tasks.length === 0 ? (
        <p style={{ textAlign: 'center', color: '#666' }}>No tasks available</p>
      ) : (
        tasks.map(task => (
          <div
            key={task.id}
            style={{
              border: '1px solid #ddd',
              padding: '20px',
              marginBottom: '15px',
              borderRadius: '8px',
              backgroundColor: '#f9f9f9',
              boxShadow: '0 2px 6px rgba(0, 0, 0, 0.1)',
              transition: 'background-color 0.3s',
            }}
            onMouseEnter={(e) => (e.target.style.backgroundColor = '#f1f1f1')}
            onMouseLeave={(e) => (e.target.style.backgroundColor = '#f9f9f9')}
          >
            <div
              style={{
                fontSize: '20px',
                fontWeight: '600',
                color: '#333',
              }}
            >
              {task.title}
            </div>
            <div
              style={{
                fontSize: '14px',
                color: '#666',
                marginBottom: '15px',
              }}
            >
              {task.description}
            </div>
            <div
              style={{
                fontSize: '14px',
                fontWeight: '500',
                color: task.completed ? '#4CAF50' : '#F44336',
                marginBottom: '15px',
              }}
            >
              Status: {task.completed ? '✅ Completed' : '❌ Incomplete'}
            </div>
            <div>
              <button
                onClick={() => toggleComplete(task.id, task.completed)}
                style={{
                  backgroundColor: '#4CAF50',
                  color: '#fff',
                  border: 'none',
                  padding: '8px 16px',
                  borderRadius: '5px',
                  cursor: 'pointer',
                  transition: 'background-color 0.3s',
                }}
                onMouseEnter={(e) => (e.target.style.backgroundColor = '#45a049')}
                onMouseLeave={(e) => (e.target.style.backgroundColor = '#4CAF50')}
              >
                Toggle
              </button>
              <button
                onClick={() => deleteTask(task.id)}
                style={{
                  backgroundColor: '#F44336',
                  color: '#fff',
                  border: 'none',
                  padding: '8px 16px',
                  borderRadius: '5px',
                  marginLeft: '10px',
                  cursor: 'pointer',
                  transition: 'background-color 0.3s',
                }}
                onMouseEnter={(e) => (e.target.style.backgroundColor = '#e53935')}
                onMouseLeave={(e) => (e.target.style.backgroundColor = '#F44336')}
              >
                Delete
              </button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}
