import React, { useState } from 'react';
import axios from 'axios';

export default function AddTaskPage() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const handleAdd = async () => {
    try {
      if (!title.trim()) {
        alert('Title is required');
        return;
      }
      await axios.post('http://localhost:5000/tasks', { title, description });
      alert('Task added!');
      setTitle('');
      setDescription('');
    } catch {
      alert('Error adding task');
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h2 className="text-xl font-bold mb-4">Add Task</h2>
      <input className="border w-full p-2 mb-2" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} />
      <textarea className="border w-full p-2 mb-2" placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} />
      <button className="bg-blue-500 text-white px-4 py-2" onClick={handleAdd}>Add</button>
    </div>
  );
}
